import { precacheAndRoute, cleanupOutdatedCaches } from 'workbox-precaching';
import { registerRoute } from 'workbox-routing';
import { NetworkFirst, CacheFirst, StaleWhileRevalidate } from 'workbox-strategies';
import { BackgroundSyncPlugin } from 'workbox-background-sync';
import { ExpirationPlugin } from 'workbox-expiration';

// Clean up old precaches automatically
cleanupOutdatedCaches();

// Precache all assets generated by the build process
precacheAndRoute([{"revision":"c8fd865b09ebf09208e4af96516cd416","url":"404.html"},{"revision":"82294021d3095bef71b1163dfa76d741","url":"analytics.js"},{"revision":"03924a0c140f09fbad9dab79e7f1e4c6","url":"index.html"},{"revision":"99d6c231d2de698298d463bce7b1139f","url":"manifest.json"},{"revision":"d19628fa347511fa3a5fbe4b09df27c9","url":"static/css/main.1694c03b.css"},{"revision":"ffea98fd3afb5e5290b6c3ea2534662e","url":"static/js/main.d3f52819.js"}] || []);

// Cache API calls with Network First strategy (try network, fallback to cache)
registerRoute(
  ({ url }) => url.pathname.startsWith('/api/'),
  new NetworkFirst({
    cacheName: 'api-cache',
    networkTimeoutSeconds: 5,
    plugins: [
      new ExpirationPlugin({
        maxEntries: 50,
        maxAgeSeconds: 5 * 60, // 5 minutes
      }),
    ],
  })
);

// Cache images with Cache First strategy and limit cache size & age
registerRoute(
  ({ request }) => request.destination === 'image',
  new CacheFirst({
    cacheName: 'image-cache',
    plugins: [
      new ExpirationPlugin({
        maxEntries: 60,
        maxAgeSeconds: 30 * 24 * 60 * 60, // 30 Days
      }),
    ],
  })
);

// Cache CSS and JS files with Stale While Revalidate (serve cache immediately, update in background)
registerRoute(
  ({ request }) =>
    request.destination === 'script' ||
    request.destination === 'style',
  new StaleWhileRevalidate({
    cacheName: 'static-resources',
  })
);

// Background sync for failed POST requests (for example, comments or form submissions)
const bgSyncPlugin = new BackgroundSyncPlugin('post-queue', {
  maxRetentionTime: 24 * 60, // Retry for max of 24 Hours (in minutes)
});

registerRoute(
  ({ url, request }) =>
    request.method === 'POST' &&
    url.origin === self.location.origin,
  new NetworkFirst({
    cacheName: 'post-requests',
    plugins: [bgSyncPlugin],
  }),
  'POST'
);

// Offline fallback for navigation requests (serve index.html)
registerRoute(
  ({ request }) => request.mode === 'navigate',
  async ({ event }) => {
    try {
      return await fetch(event.request);
    } catch (error) {
      return caches.match('/index.html');
    }
  }
);
